import React, { useState } from 'react';
import axios from 'axios';
import MapComponent from './MapComponent';
import './App.css';

const API_BASE_URL = 'http://localhost:8080/api';

function App() {
  const [user, setUser] = useState(null);
  const [zones, setZones] = useState([]);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    username: ''
  });
  const [zoneData, setZoneData] = useState({
    name: '',
    latitude: null,
    longitude: null,
    radius: '100',
    mode: 'silent'
  });
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);

  const api = axios.create({
    baseURL: API_BASE_URL,
  });

  api.interceptors.request.use((config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  });

  const handleAuth = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const endpoint = isLogin ? '/auth/login' : '/auth/register';
      const payload = isLogin 
        ? { email: formData.email, password: formData.password }
        : formData;

      const response = await api.post(endpoint, payload);
      localStorage.setItem('token', response.data.token);
      setUser(response.data.user);
      fetchZones();
    } catch (error) {
      alert(error.response?.data?.message || 'Authentication failed');
    } finally {
      setLoading(false);
    }
  };

  const fetchZones = async () => {
    try {
      const response = await api.get('/zones');
      setZones(response.data);
    } catch (error) {
      console.error('Error fetching zones:', error);
    }
  };

  const createZone = async (e) => {
    e.preventDefault();
    if (!zoneData.latitude || !zoneData.longitude) {
      alert('Please select a location on the map');
      return;
    }
    try {
      const response = await api.post('/zones', {
        ...zoneData,
        latitude: zoneData.latitude,
        longitude: zoneData.longitude,
        radius: parseInt(zoneData.radius)
      });
      setZones([...zones, response.data]);
      setZoneData({ name: '', latitude: null, longitude: null, radius: '100', mode: 'silent' });
      alert('Zone created successfully!');
    } catch (error) {
      alert('Failed to create zone');
    }
  };

  const handleLocationSelect = (location) => {
    setZoneData({
      ...zoneData,
      latitude: location.latitude,
      longitude: location.longitude
    });
  };

  const deleteZone = async (id) => {
    try {
      await api.delete(`/zones/${id}`);
      setZones(zones.filter(zone => zone._id !== id));
    } catch (error) {
      alert('Failed to delete zone');
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setZones([]);
  };

  if (!user) {
    return (
      <div className="container">
        <div className="auth-form">
          <h1>Silent Zone</h1>
          <h2>{isLogin ? 'Login' : 'Register'}</h2>
          <form onSubmit={handleAuth}>
            {!isLogin && (
              <input
                type="text"
                placeholder="Username"
                value={formData.username}
                onChange={(e) => setFormData({...formData, username: e.target.value})}
                required
              />
            )}
            <input
              type="email"
              placeholder="Email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              required
            />
            <button type="submit" disabled={loading}>
              {loading ? 'Loading...' : (isLogin ? 'Login' : 'Register')}
            </button>
          </form>
          <p>
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button type="button" onClick={() => setIsLogin(!isLogin)}>
              {isLogin ? 'Register' : 'Login'}
            </button>
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h1>Silent Zone Dashboard</h1>
        <div>
          <span>Welcome, {user.username}!</span>
          <button onClick={logout}>Logout</button>
        </div>
      </header>

      <div className="content">
        <div className="zones-section">
          <h2>My Zones ({zones.length})</h2>
          <div className="zones-grid">
            {zones.map(zone => (
              <div key={zone._id} className="zone-card">
                <h3>{zone.name}</h3>
                <p>Mode: {zone.mode === 'silent' ? 'ðŸ”‡ Silent' : 'ðŸ“³ Vibrate'}</p>
                <p>Radius: {zone.radius}m</p>
                <p>Location: {zone.location.coordinates[1].toFixed(4)}, {zone.location.coordinates[0].toFixed(4)}</p>
                <button onClick={() => deleteZone(zone._id)}>Delete</button>
              </div>
            ))}
          </div>
        </div>

        <div className="add-zone-section">
          <h2>Add New Zone</h2>
          <form onSubmit={createZone}>
            <input
              type="text"
              placeholder="Zone Name"
              value={zoneData.name}
              onChange={(e) => setZoneData({...zoneData, name: e.target.value})}
              required
            />
            <MapComponent
              onLocationSelect={handleLocationSelect}
              radius={parseInt(zoneData.radius) || 100}
            />
            <input
              type="number"
              placeholder="Radius (meters)"
              value={zoneData.radius}
              onChange={(e) => setZoneData({...zoneData, radius: e.target.value})}
              required
            />
            <select
              value={zoneData.mode}
              onChange={(e) => setZoneData({...zoneData, mode: e.target.value})}
            >
              <option value="silent">Silent</option>
              <option value="vibrate">Vibrate</option>
            </select>
            <button type="submit">Create Zone</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
