import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MapComponent from './MapComponent';
import './App.css';

const API_BASE_URL = 'http://localhost:8080/api';

function App() {
  const [user, setUser] = useState(null);
  const [zones, setZones] = useState([]);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    username: ''
  });
  const [zoneData, setZoneData] = useState({
    name: '',
    latitude: null,
    longitude: null,
    radius: 100,
    mode: 'silent'
  });
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [userLocation, setUserLocation] = useState(null);
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);

  const api = axios.create({
    baseURL: API_BASE_URL,
  });

  api.interceptors.request.use((config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  });

  const handleAuth = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const endpoint = isLogin ? '/auth/login' : '/auth/register';
      const payload = isLogin 
        ? { email: formData.email, password: formData.password }
        : formData;

      const response = await api.post(endpoint, payload);
      localStorage.setItem('token', response.data.token);
      setUser(response.data.user);
      fetchZones();
    } catch (error) {
      alert(error.response?.data?.message || 'Authentication failed');
    } finally {
      setLoading(false);
    }
  };

  const fetchZones = async () => {
    try {
      const response = await api.get('/zones');
      setZones(response.data);
    } catch (error) {
      console.error('Error fetching zones:', error);
    }
  };

  const createZone = async (e) => {
    e.preventDefault();
    if (!zoneData.latitude || !zoneData.longitude) {
      alert('Please select a location on the map');
      return;
    }
    try {
      const response = await api.post('/zones', {
        ...zoneData,
        latitude: zoneData.latitude,
        longitude: zoneData.longitude,
        radius: zoneData.radius
      });
      setZones([...zones, response.data]);
      setZoneData({ name: '', latitude: null, longitude: null, radius: 100, mode: 'silent' });
      alert('Zone created successfully!');
    } catch (error) {
      alert('Failed to create zone');
    }
  };

  const handleLocationSelect = (location) => {
    setZoneData({
      ...zoneData,
      latitude: location.latitude,
      longitude: location.longitude
    });
  };

  const getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          });
        },
        (error) => {
          console.log('Location access denied');
          // Default to Hyderabad
          setUserLocation({ latitude: 17.3850, longitude: 78.4867 });
        }
      );
    } else {
      // Default to Hyderabad
      setUserLocation({ latitude: 17.3850, longitude: 78.4867 });
    }
  };

  useEffect(() => {
    getUserLocation();
  }, []);

  const deleteZone = async (id) => {
    try {
      await api.delete(`/zones/${id}`);
      setZones(zones.filter(zone => zone._id !== id));
    } catch (error) {
      alert('Failed to delete zone');
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setZones([]);
  };

  if (!user) {
    return (
      <div className="container">
        <div className="auth-form">
          <h1>Silent Zone</h1>
          <h2>{isLogin ? 'Login' : 'Register'}</h2>
          <form onSubmit={handleAuth}>
            {!isLogin && (
              <input
                type="text"
                placeholder="Username"
                value={formData.username}
                onChange={(e) => setFormData({...formData, username: e.target.value})}
                required
              />
            )}
            <input
              type="email"
              placeholder="Email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              required
            />
            <button type="submit" disabled={loading}>
              {loading ? 'Loading...' : (isLogin ? 'Login' : 'Register')}
            </button>
          </form>
          <p>
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button type="button" onClick={() => setIsLogin(!isLogin)}>
              {isLogin ? 'Register' : 'Login'}
            </button>
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <header className="modern-header">
        <div className="header-content">
          <div className="logo-section">
            <div className="logo-icon">🔇</div>
            <h1 className="app-title">Silent Zone</h1>
          </div>
          <div className="user-section">
            <div className="user-profile" onClick={() => setShowUserMenu(!showUserMenu)}>
              <div className="user-avatar">
                {user.username.charAt(0).toUpperCase()}
              </div>
              <span className="user-name">{user.username}</span>
              <div className="dropdown-arrow">▼</div>
            </div>
            {showUserMenu && (
              <div className="user-menu">
                <div className="menu-item">Profile</div>
                <div className="menu-item">Settings</div>
                <div className="menu-divider"></div>
                <div className="menu-item logout" onClick={logout}>Logout</div>
              </div>
            )}
          </div>
        </div>
      </header>

      <main className="main-content">
        <div className="content-container">
          <div className="zones-section">
            <div className="section-header">
              <h2>My Silent Zones</h2>
              <span className="zone-count">{zones.length} zones</span>
            </div>

            {zones.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">📍</div>
                <h3>Your Zones Will Appear Here</h3>
                <p>Add a zone to automatically silence your phone at work, school, or any location.</p>
                <button className="cta-button" onClick={() => document.getElementById('zone-name').focus()}>
                  + Add Your First Zone
                </button>
              </div>
            ) : (
              <div className="zones-grid">
                {zones.map(zone => (
                  <div key={zone._id} className="modern-zone-card">
                    <div className="zone-header">
                      <div className="zone-icon">
                        {zone.mode === 'silent' ? '🔇' : '📳'}
                      </div>
                      <div className="zone-info">
                        <h3>{zone.name}</h3>
                        <p className="zone-mode">
                          {zone.mode === 'silent' ? 'Silent Mode' : 'Vibrate Mode'}
                        </p>
                      </div>
                    </div>
                    <div className="zone-details">
                      <div className="detail-item">
                        <span className="detail-label">Radius</span>
                        <span className="detail-value">{zone.radius}m</span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">Location</span>
                        <span className="detail-value">
                          {zone.location.coordinates[1].toFixed(4)}, {zone.location.coordinates[0].toFixed(4)}
                        </span>
                      </div>
                    </div>
                    <button className="delete-button" onClick={() => deleteZone(zone._id)}>
                      Delete Zone
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="add-zone-section">
            <div className="form-card">
              <h2>Create New Zone</h2>
              <form onSubmit={createZone} className="modern-form">
                <div className="form-group">
                  <label htmlFor="zone-name">Zone Name</label>
                  <input
                    id="zone-name"
                    type="text"
                    placeholder="e.g., Office, Library, Hospital"
                    value={zoneData.name}
                    onChange={(e) => setZoneData({...zoneData, name: e.target.value})}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Select Location</label>
                  <p className="form-hint">Click on the map to choose your silent zone location</p>
                  <MapComponent
                    onLocationSelect={handleLocationSelect}
                    radius={zoneData.radius}
                    userLocation={userLocation}
                  />
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="radius">Radius (meters)</label>
                    <input
                      id="radius"
                      type="range"
                      min="10"
                      max="1000"
                      value={zoneData.radius}
                      onChange={(e) => setZoneData({...zoneData, radius: parseInt(e.target.value)})}
                      className="radius-slider"
                    />
                    <div className="radius-display">{zoneData.radius}m</div>
                  </div>

                  <div className="form-group">
                    <label htmlFor="mode">Audio Mode</label>
                    <select
                      id="mode"
                      value={zoneData.mode}
                      onChange={(e) => setZoneData({...zoneData, mode: e.target.value})}
                    >
                      <option value="silent">🔇 Silent Mode</option>
                      <option value="vibrate">📳 Vibrate Mode</option>
                    </select>
                  </div>
                </div>

                <button type="submit" className="create-button">
                  <span>Create Zone</span>
                  <div className="button-icon">+</div>
                </button>
              </form>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
