import React, { useState, useEffect, useRef } from 'react';

const MapComponent = ({ onLocationSelect, radius = 100 }) => {
  const [position, setPosition] = useState(null);
  const [mapLoaded, setMapLoaded] = useState(false);
  const [leafletLoaded, setLeafletLoaded] = useState(false);
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const markerRef = useRef(null);
  const circleRef = useRef(null);

  useEffect(() => {
    // Check if Leaflet is already loaded
    if (window.L) {
      setLeafletLoaded(true);
      return;
    }

    // Load Leaflet CSS
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css';
    link.integrity = 'sha256-p4NxAoJBhIIN+hmNHrzRCf9tD/miZyoHS5obTRR9BMY=';
    link.crossOrigin = '';
    document.head.appendChild(link);

    // Load Leaflet JS
    const script = document.createElement('script');
    script.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js';
    script.integrity = 'sha256-20nQCchB9co0qIjJZRGuk2/Z9VM+kNiyxNV1lvTlZBo=';
    script.crossOrigin = '';
    script.onload = () => {
      setLeafletLoaded(true);
    };
    document.head.appendChild(script);

    return () => {
      // Cleanup
      if (mapInstance.current) {
        mapInstance.current.remove();
        mapInstance.current = null;
      }
    };
  }, []);

  useEffect(() => {
    if (leafletLoaded && mapRef.current && !mapLoaded) {
      // Small delay to ensure DOM is ready
      setTimeout(() => {
        initializeMap();
      }, 100);
    }
  }, [leafletLoaded, mapLoaded]);

  const initializeMap = () => {
    if (!window.L || !mapRef.current || mapInstance.current) return;

    try {
      // Initialize map
      const map = window.L.map(mapRef.current, {
        center: [37.7749, -122.4194],
        zoom: 13,
        zoomControl: true,
        scrollWheelZoom: true
      });

      // Add tile layer
      window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '© <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
        maxZoom: 19
      }).addTo(map);

      // Handle map clicks
      map.on('click', (e) => {
        const { lat, lng } = e.latlng;

        // Remove existing marker and circle
        if (markerRef.current) {
          map.removeLayer(markerRef.current);
        }
        if (circleRef.current) {
          map.removeLayer(circleRef.current);
        }

        // Add new marker
        markerRef.current = window.L.marker([lat, lng]).addTo(map);

        // Add circle
        circleRef.current = window.L.circle([lat, lng], {
          radius: radius,
          fillColor: '#007AFF',
          fillOpacity: 0.2,
          color: '#007AFF',
          weight: 2
        }).addTo(map);

        const newPosition = { latitude: lat, longitude: lng };
        setPosition(newPosition);

        if (onLocationSelect) {
          onLocationSelect(newPosition);
        }
      });

      // Ensure map renders properly
      setTimeout(() => {
        map.invalidateSize();
      }, 100);

      mapInstance.current = map;
      setMapLoaded(true);
    } catch (error) {
      console.error('Error initializing map:', error);
    }
  };

  return (
    <div style={{ marginBottom: '15px' }}>
      <div 
        ref={mapRef} 
        style={{ 
          height: '300px', 
          width: '100%',
          border: '2px solid #ddd',
          borderRadius: '8px',
          backgroundColor: '#f0f0f0',
          position: 'relative',
          overflow: 'hidden'
        }}
      >
        {!mapLoaded && (
          <div style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            color: '#666',
            fontSize: '14px'
          }}>
            Loading map...
          </div>
        )}
      </div>
      {position && (
        <p style={{ 
          fontSize: '12px', 
          color: '#007AFF', 
          marginTop: '8px',
          fontFamily: 'monospace',
          backgroundColor: '#f8f9fa',
          padding: '4px 8px',
          borderRadius: '4px',
          display: 'inline-block'
        }}>
          📍 {position.latitude.toFixed(6)}, {position.longitude.toFixed(6)}
        </p>
      )}
      <p style={{ 
        fontSize: '12px', 
        color: '#666', 
        marginTop: '5px',
        fontStyle: 'italic'
      }}>
        {mapLoaded ? 'Click on the map to select a location' : 'Loading OpenStreetMap...'}
      </p>
    </div>
  );
};

export default MapComponent;
