import React, { useState, useEffect, useRef } from 'react';

const MapComponent = ({ onLocationSelect, radius = 100 }) => {
  const [position, setPosition] = useState(null);
  const [map, setMap] = useState(null);
  const mapRef = useRef(null);

  useEffect(() => {
    // Load Leaflet dynamically
    const loadLeaflet = async () => {
      if (typeof window !== 'undefined' && !window.L) {
        // Load Leaflet CSS
        const link = document.createElement('link');
        link.rel = 'stylesheet';
        link.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css';
        document.head.appendChild(link);

        // Load Leaflet JS
        const script = document.createElement('script');
        script.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js';
        script.onload = initMap;
        document.head.appendChild(script);
      } else if (window.L) {
        initMap();
      }
    };

    const initMap = () => {
      if (mapRef.current && window.L && !map) {
        const leafletMap = window.L.map(mapRef.current).setView([37.7749, -122.4194], 13);

        window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: '© OpenStreetMap contributors'
        }).addTo(leafletMap);

        let marker = null;
        let circle = null;

        leafletMap.on('click', (e) => {
          const { lat, lng } = e.latlng;

          // Remove existing marker and circle
          if (marker) leafletMap.removeLayer(marker);
          if (circle) leafletMap.removeLayer(circle);

          // Add new marker and circle
          marker = window.L.marker([lat, lng]).addTo(leafletMap);
          circle = window.L.circle([lat, lng], {
            radius: radius,
            fillColor: 'blue',
            fillOpacity: 0.2,
            color: 'blue',
            weight: 2
          }).addTo(leafletMap);

          const newPosition = { latitude: lat, longitude: lng };
          setPosition(newPosition);
          if (onLocationSelect) {
            onLocationSelect(newPosition);
          }
        });

        setMap(leafletMap);
      }
    };

    loadLeaflet();

    return () => {
      if (map) {
        map.remove();
      }
    };
  }, [map, onLocationSelect, radius]);

  return (
    <div style={{ marginBottom: '15px' }}>
      <div 
        ref={mapRef} 
        style={{ 
          height: '300px', 
          width: '100%',
          border: '1px solid #ddd',
          borderRadius: '8px'
        }}
      />
      {position && (
        <p style={{ fontSize: '12px', color: '#666', marginTop: '5px' }}>
          Selected: {position.latitude.toFixed(6)}, {position.longitude.toFixed(6)}
        </p>
      )}
      <p style={{ fontSize: '12px', color: '#999', marginTop: '5px' }}>
        Click on the map to select a location
      </p>
    </div>
  );
};

export default MapComponent;
